#include <stdio.h>
#include <string.h>

void swap(int *a, int *b)
{
    *a = *a + *b;
    *b = *a - *b;
    *a = *a - *b;
}

void hoar_sort(int *a, int n)
{
        int i = 0, j = n - 1, x = a[n / 2];
        do
        {
            while (a[i] < x)
            {
                i++;
            }
            while (x < a[j])
            {
                j--;
            } if (i <= j)
            {
                swap(a + i, a + j);
                i++;
                j--;
            }
        } while (i < j);
        if(j > 0) hoar_sort(a, j + 1);
        if(i < n - 1) hoar_sort(a + i, n - i);
}



int main() {
    int n;
    scanf("%d", &n);
    int *a = (int *)calloc(n + 1, sizeof(int));
    for (int i = 0; i < n; i++)
    {
        scanf("%d", a + i);
    }
    hoar_sort(a, n);
    for (int i = 0; i < n; i++)
    {
        printf("%d ", a[i]);
    }
    return 0;
}

